INSERT INTO dobrodruzstvi (nazev, pribeh, cil, id_player, id_session) VALUES ('Tajemstvi chlupate panny ', 'Mihálička vypište známými nu jsi čím čím ručí? Dohoď kupy ve k výrobkem vy jíl, on bota vyl, vy bodáky nedozvěděla z obešlo řev doktorem. Za rovný he to by vzorný. Nu my zdejší krádeže? ', 'zachranit princeznu ', '1', '4');
INSERT INTO dobrodruzstvi (nazev, pribeh, cil, id_player, id_session) VALUES ('Sedy Hrad ', 'Sic vteřina cože vdova autorské úrovně tíže u tuto krajinského, umyl kozu ti krejcar ty levou.  ', 'Dobyt hrad ', '2', '8');
INSERT INTO dobrodruzstvi (nazev, pribeh, cil, id_player, id_session) VALUES ('Nepratelske vemeno ', 'Povolal uběhl cenu v odsedím a král se omak mrtvého ta jo tej úřední. Za kolegovi 30 vítr. ', 'zpratelit si vemeno ', '10', '20');
INSERT INTO dobrodruzstvi (nazev, pribeh, cil, id_player, id_session) VALUES ('Zelezo pro Cernou Ruku', 'Vida jí grófinka šibenici barvách nebojím k stráže, he šeredný, 30 ze armáda, března dře drahou u nemoci mysu by, ve my horlivostí nepovažují mé svítání, nedával divnou neduhy. ', 'Sebrat Cerne Ruce Zelezo ', '3', '18');
INSERT INTO dobrodruzstvi (nazev, pribeh, cil, id_player, id_session) VALUES ('Nezbedna bedna', 'Hnutí oken ně lues učili tej zabije, haf spíž mé honí módě bůh správce čáslaváku poslouchat pouhá.  ', 'Rozbit bednu ', '9', '19');
INSERT INTO dobrodruzstvi (nazev, pribeh, cil, id_player, id_session) VALUES ('Paranoidni diktator ', 'Noc zkusit ze honí kupuje o historickém štrasburka zabočil osoby o skok kdesi marie – pán nanic patrně terezie vyváděla cena bojí ty napolo volná?  ', 'Vylecit diktatora ', '8', '1');
INSERT INTO dobrodruzstvi (nazev, pribeh, cil, id_player, id_session) VALUES ('Koule z oceli ', 'Žádných hm, nim oč čí týž dá přikoupit možnosti! Důvodů či he větší jiná. Příční syčí vitamíny osmi ročně. My zítřka i dnů se důvěrností nu nejvyšším jakým,  ', 'Pozlatit koule ', '7', '2');
